package main.java.webservice.contract.service;

import javax.jws.WebService;

/**
 * 指定和接口一样的targetNameSpace和endpointInterface和wsdlLocation
 * @author ddf 2016年9月27日下午5:08:57
 *
 */
@WebService(endpointInterface="main.java.webservice.contract.service.MyService",
			targetNamespace="http://www.example.org/myService/",
			wsdlLocation="META-INF/wsdl/myService.wsdl")
public class MyServiceImpl implements MyService {

	@Override
	public AddResponse add(AddRequest addRequest) {
		AddResponse ar = new AddResponse();
		int result = addRequest.getNum1() + addRequest.getNum2();
		ar.setAddResult(result);
		return ar;
	}

	@Override
	public DivideResponse divide(DivideRequest divideRequest)  throws ServiceException{
		DivideResponse dr = new DivideResponse();
		if(divideRequest != null && divideRequest.getNum2() == 0) {
			ServiceExceptionType ty = new ServiceExceptionType();
			// 这一块没搞懂为什么会同时生成ServiceException和..
			ty.setMessage("除数不能为02");
			throw new ServiceException("除数不能为0", ty);
		}
		dr.setDivideResult((divideRequest.getNum1() 
				/ divideRequest.getNum2()));
		System.out.println("divide Service:" + dr.getDivideResult());
		return dr;
	}


}
